mapper.select:
select
  id, user_id, alarm_id, is_read, device_alias, switch_alias, msg_content
from meiot_msg.user_alarm


        and id = #{id}


        and user_id = #{userId}


        and alarm_id = #{alarmId}


        and is_read = #{isRead}


        and device_alias = #{deviceAlias}


        and switch_alias = #{switchAlias}


        and msg_content = #{msgContent}
