mapper.select:
SELECT ROUND(${function}(${tableName}),1) AS value,
s_month
s_day
s_time AS name FROM pc_${tableName}_${timeName} WHERE project_id = #{projectId} AND s_year = #{years}
AND switch_sn = #{switchSn}
AND switch_sn in
#{item.masterSn}
AND s_month = #{months}
AND s_day = #{hours}
GROUP BY
s_month
s_day
s_time

SELECT
ROUND(${function}(a.${tableName}),1) AS value,

AS name
FROM
(
SELECT

${tableName}
FROM pc_${tableName}_${timeName}
WHERE id = 0


	UNION all
	SELECT

	${tableName}
	FROM pc_${tableName}_${timeName}
	WHERE serial_number = #{item.serialNumber}
	AND switch_sn = #{item.masterSn}

		AND s_year = #{years}


		AND s_month = #{months}


		AND s_day = #{hours}


		AND project_id = #{projectId}



) a
GROUP BY

SELECT user_id AS userId, serial_number AS serialNumber, switch_index AS switchIndex, switch_sn AS switchSn, s_year AS year, s_month AS month,
s_day AS day,
project_id AS projectId FROM ${platform}_${tableName}_${timeName} WHERE 1=1
AND s_year = #{sYear}
AND s_month = #{sMonth}
AND s_day = #{sDay}
GROUP BY serial_number,switch_sn,project_id, user_id

SELECT
leakage AS value,
unix_timestamp(create_time) * 1000 AS name
FROM
pc_leakage_hours
WHERE

	serial_number = #{pcLeakageHours.serialNumber}


	AND
	switch_sn = #{pcLeakageHours.switchSn}


	AND
	user_id = #{pcLeakageHours.userId }


	AND
	project_id = #{pcLeakageHours.projectId}



	AND unix_timestamp(create_time) * 1000 between #{startTime} AND #{endTime}

ORDER BY create_time
