mapper.select:
SELECT
	ROUND(${function}(${tableName}),1) AS value
FROM
	pc_${tableName}_${nowTimeName}
WHERE
	project_id = #{projectId}
AND
	s_year = #{years}


	AND
	switch_sn = #{switchSn}


	AND switch_sn in

		#{item.masterSn}



AND
	s_month = #{months}


AND
	s_day = #{hours}

GROUP BY

s_month


s_day

SELECT
ROUND(${function}(a.${tableName}),1) AS value
FROM(
SELECT

${tableName}
FROM pc_${tableName}_${nowTimeName} WHERE id = 0


	UNION all
	SELECT

	${tableName}
	FROM pc_${tableName}_${nowTimeName}
	WHERE serial_number = #{item.serialNumber}
	AND switch_sn = #{item.masterSn}



		AND s_year = #{years}


		AND s_month = #{months}


		AND s_day = #{hours}


		AND project_id = #{projectId}




) a
GROUP BY

SELECT
id
FROM
${platform}_${tableName}_${timeName}
WHERE
project_id = #{projectId}

	AND
	serial_number = #{serialNumber}



	AND
	switch_sn = #{oldSwitchSn}

mapper.update:
UPDATE
${platform}_${tableName}_${timeName}
SET

switch_sn = #{newSwitchSn}

WHERE
project_id = #{projectId}

	AND
	serial_number = #{serialNumber}



	AND
	switch_sn = #{oldSwitchSn}
